package main

import (
	"github.com/labstack/echo/v4"
	"github.com/labstack/echo/v4/middleware"
	"net/http"
)

type mjHandler struct {
	log                  echo.Logger
	tenhouMessageReceiver *tenhou.MessageReceiver
	majsoulMessageQueue   chan []byte
}

func (h *mjHandler) analysisTenhou(c echo.Context) error {
	body, err := c.Request().GetBody()
	if err != nil {
		h.log.Error(err)
		return err
	}
	defer body.Close()

	_, err = h.tenhouMessageReceiver.Receive(body)
	if err != nil {
		h.log.Error(err)
		return err
	}

	return c.String(http.StatusOK, "ok")
}

func (h *mjHandler) analysisMajsoul(c echo.Context) error {
	body, err := c.Request().GetBody()
	if err != nil {
		h.log.Error(err)
		return err
	}
	defer body.Close()

	h.majsoulMessageQueue <- body
	return c.String(http.StatusOK, "ok")
}

func runServer(isHTTPS bool, port int) error {
	e := echo.New()
	e.Use(middleware.Logger())
	e.Use(middleware.Recover())

	h := &mjHandler{
		log:                  e.Logger,
		tenhouMessageReceiver: tenhou.NewMessageReceiver(),
		majsoulMessageQueue:   make(chan []byte),
	}

	e.POST("/tenhou", h.analysisTenhou)
	e.POST("/majsoul", h.analysisMajsoul)

	if isHTTPS {
		return e.StartTLS(":"+strconv.Itoa(port), "cert.pem", "key.pem")
	}
	return e.Start(":" + strconv.Itoa(port))
}
