package main

import (
	"net/http"
	"net/http/httputil"
	"net/url"
	"strings"

	"github.com/labstack/echo/v4"
)

type Host struct {
	Echo *echo.Echo
}

func main() {
	// Initialize Echo instances for each host
	host1 := echo.New()
	host2 := echo.New()

	// Set up routes for each host
	host1.GET("/", func(c echo.Context) error {
		return c.String(http.StatusOK, "Hello from host1")
	})

	host2.GET("/", func(c echo.Context) error {
		return c.String(http.StatusOK, "Hello from host2")
	})

	// Create a map of hostnames to Echo instances
	hosts := map[string]*Host{
		"host1.example.com": {Echo: host1},
		"host2.example.com": {Echo: host2},
	}

	// Create an Echo instance for the reverse proxy
	proxy := echo.New()

	// Set up the reverse proxy route
	proxy.Any("/*", func(c echo.Context) error {
		// Get the host header from the request
		host := c.Request().Host

		// Get the Echo instance for the host
		h, ok := hosts[host]
		if !ok {
			return c.String(http.StatusNotFound, "Host not found")
		}

		// Create a reverse proxy to the Echo instance
		proxy := httputil.NewSingleHostReverseProxy(&url.URL{
			Scheme: "http",
			Host:   host,
		})

		// Modify the request to use the correct host
		proxy.Director = func(req *http.Request) {
			req.Host = host
			req.URL.Scheme = "http"
			req.URL.Host = host
		}

		// Proxy the request to the Echo instance
		proxy.ServeHTTP(c.Response(), c.Request())

		return nil
	})

	// Start the reverse proxy server
	proxy.Start(":8080")
}
