package main

import (
	"fmt"
	"net/http"
	"os"
	"os/signal"
	"syscall"
	"time"

	"github.com/labstack/echo/v4"
	"github.com/labstack/echo/v4/middleware"
)

var (
	workflowKey = "workflow-key"
	builderKey  = "builder-key"
)

func main() {
	startWorkflowServer("localhost", 8080)
}

func startWorkflowServer(host string, port uint64) {
	e := echo.New()

	// Middleware
	e.Use(middleware.Logger())
	e.Use(middleware.Recover())

	// Routes
	e.GET("/secrets", secretsHandler)
	e.POST("/trigger", triggerHandler)

	// Start server
	go func() {
		if err := e.Start(fmt.Sprintf("%s:%d", host, port)); err != nil && err != http.ErrServerClosed {
			e.Logger.Fatal("shutting down the server")
		}
	}()

	// Wait for interrupt signal to gracefully shutdown the server with a timeout of 10 seconds.
	// Use a buffered channel to avoid missing signals as recommended for signal.Notify
	quit := make(chan os.Signal, 1)
	signal.Notify(quit, os.Interrupt, syscall.SIGTERM)
	<-quit
	fmt.Println("Shutting down server...")

	// The context is used to inform the server it has 10 seconds to finish
	// the request it is currently handling
	ctx, cancel := context.WithTimeout(context.Background(), 10*time.Second)
	defer cancel()
	if err := e.Shutdown(ctx); err != nil {
		e.Logger.Fatal("Server forced to shutdown:", err)
	}

	fmt.Println("Server exiting")
}

func secretsHandler(c echo.Context) error {
	if c.Request().Header.Get("Authorization") != workflowKey {
		return c.JSON(http.StatusUnauthorized, "Unauthorized")
	}

	secretsHit = true
	return c.JSON(http.StatusOK, "Secrets accessed")
}

func triggerHandler(c echo.Context) error {
	if c.Request().Header.Get("Authorization") != builderKey {
		return c.JSON(http.StatusUnauthorized, "Unauthorized")
	}

	triggerHit = true
	return c.JSON(http.StatusOK, "Triggered")
}
