)

type Options struct {
	ListenAddr string
}

func Run(op *Options) error {
	r := gin.Default()
	r.GET("/api/v1/ping", func(c *gin.Context) {
		c.JSON(http.StatusOK, gin.H{
			"message": "pong",
		})
	})

	r.GET("/api/v1/info", func(c *gin.Context) {
		c.JSON(http.StatusOK, gin.H{
			"message": "info",
		})
	})

	var counter int
	var mu sync.Mutex

	r.GET("/api/v1/counter", func(c *gin.Context) {
		mu.Lock()
		defer mu.Unlock()
		c.JSON(http.StatusOK, gin.H{
			"counter": counter,
		})
	})

	r.PUT("/api/v1/counter/increment", func(c *gin.Context) {
		mu.Lock()
		defer mu.Unlock()
		counter++
		c.JSON(http.StatusOK, gin.H{
			"message": "counter incremented",
		})
	})

	r.PUT("/api/v1/counter/reset", func(c *gin.Context) {
		mu.Lock()
		defer mu.Unlock()
		counter = 0
		c.JSON(http.StatusOK, gin.H{
			"message": "counter reset",
		})
	})

	klog.Infof("Starting server on %s", op.ListenAddr)
	return r.Run(op.ListenAddr)
}
