	limiter := ratelimit.NewLeakyBucket(rps, rps)

	// Define the rate limiting middleware
	r.Use(func(c *gin.Context) {
		// Check if the rate limiter allows the request
		if !limiter.Allow() {
			// If not, return a 429 Too Many Requests response
			c.JSON(http.StatusTooManyRequests, gin.H{"error": "Too Many Requests"})
			c.Abort()
			return
		}
		c.Next()
	})

	// Define the "/rate" endpoint
	r.GET("/rate", func(c *gin.Context) {
		c.JSON(http.StatusOK, gin.H{"message": "Rate limit is working"})
	})

	// Log the current rate limit setting
	log.Printf("Rate limit set to %d requests per second\n", rps)

	// Start the HTTP server on port 8080
	if err := r.Run(":8080"); err != nil {
		log.Fatalf("Failed to start server: %v", err)
	}
}

func main() {
	// Set the rate limit to 5 requests per second
	rps := 5
	ginRun(rps)
}
