type DNSEndpoint struct {
	Addr string
}

func (d *DNSEndpoint) Exchange(ctx context.Context, payload, buf []byte) (n int, err error) {
	conn, err := net.Dial("udp", d.Addr)
	if err != nil {
		return 0, err
	}
	defer conn.Close()

	if deadline, ok := ctx.Deadline(); ok {
		conn.SetDeadline(deadline)
	}

	transactionID := rand.Uint16()
	payload[0] = byte(transactionID >> 8)
	payload[1] = byte(transactionID)

	_, err = conn.Write(payload)
	if err != nil {
		return 0, err
	}

	n, err = conn.Read(buf)
	if err != nil {
		return 0, err
	}

	if buf[0] != byte(transactionID>>8) || buf[1] != byte(transactionID) {
		return 0, ErrInvalidTransactionID
	}

	return n, nil
}
