package server

import (
	"net"
	"sync"
	"time"
)

type Server struct {
	Addr         string
	Net          string
	Handler      Handler
	ReadTimeout  time.Duration
	WriteTimeout time.Duration
	IdleTimeout  func() time.Duration
	lock         sync.RWMutex
	started      bool
	shutdown     chan struct{}
	conns        map[net.Conn]struct{}
}

func (s *Server) ListenAndServe() error {
	if s.isStarted() {
		return nil
	}
	s.init()
	defer s.shutdown()
	return s.serveTCP()
}

func (s *Server) serveTCP() error {
	ln, err := net.Listen(s.Net, s.Addr)
	if err != nil {
		return err
	}
	defer ln.Close()
	for {
		select {
		case <-s.shutdown:
			return nil
		default:
			conn, err := ln.Accept()
			if err != nil {
				return err
			}
			go s.serveTCPConn(&sync.WaitGroup{}, conn)
		}
	}
}

func (s *Server) serveTCPConn(wg *sync.WaitGroup, conn net.Conn) {
	wg.Add(1)
	defer wg.Done()
	defer conn.Close()
	s.lock.Lock()
	s.conns[conn] = struct{}{}
	s.lock.Unlock()
	defer func() {
		s.lock.Lock()
		delete(s.conns, conn)
		s.lock.Unlock()
	}()
	s.Handler.ServeTCP(conn)
}

func (s *Server) readTCP(conn net.Conn, timeout time.Duration) ([]byte, error) {
	conn.SetReadDeadline(time.Now().Add(timeout))
	defer conn.SetReadDeadline(time.Time{})
	return read(conn)
}

func (s *Server) init() {
	s.lock.Lock()
	defer s.lock.Unlock()
	s.started = true
	s.shutdown = make(chan struct{})
	s.conns = make(map[net.Conn]struct{})
}

func (s *Server) isStarted() bool {
	s.lock.RLock()
	defer s.lock.RUnlock()
	return s.started
}

func (s *Server) getIdleTimeout() time.Duration {
	return s.IdleTimeout()
}

func (s *Server) getReadTimeout() time.Duration {
	return s.ReadTimeout
}
