package main

import (
	"bufio"
	"fmt"
	"io"
	"net"
	"net/http"
	"net/url"
	"strings"
)

type HTTP struct {
	addr   string
	user   string
	pass   string
	pretend bool
	proxy  proxy.Proxy
}

func (h *HTTP) ListenAndServe() {
	l, err := net.Listen("tcp", h.addr)
	if err != nil {
		fmt.Println("Error listening:", err.Error())
		return
	}
	defer l.Close()
	fmt.Println("Listening on", h.addr)
	for {
		conn, err := l.Accept()
		if err != nil {
			fmt.Println("Error accepting connection:", err.Error())
			return
		}
		go h.Serve(conn)
	}
}

func (h *HTTP) Serve(conn net.Conn) {
	defer conn.Close()
	conn.SetKeepAlive(true)
	req, err := http.ReadRequest(bufio.NewReader(conn))
	if err != nil {
		fmt.Println("Error reading request:", err.Error())
		return
	}
	if h.user != "" && h.pass != "" {
		if !h.auth(req) {
			conn.Write([]byte("HTTP/1.1 401 Unauthorized\r\nWWW-Authenticate: Basic realm=\"Restricted\"\r\n\r\n"))
			return
		}
	}
	if req.Method == "CONNECT" {
		h.servHTTPS(req, conn)
	} else {
		h.servRequest(req, h.proxy)
	}
}

func (h *HTTP) servRequest(req *http.Request, p *proxy.Conn) {
	if h.pretend {
		p.Write([]byte("HTTP/1.1 404 Not Found\r\n\r\n"))
		return
	}
	req.URL.Scheme = "http"
	req.URL.Host = req.Host
	req.RequestURI = ""
	p.Write(req.Header.Write(p))
	p.Write([]byte("\r\n"))
	if req.Body != nil {
		io.Copy(p, req.Body)
	}
	resp, err := http.ReadResponse(bufio.NewReader(p), req)
	if err != nil {
		fmt.Println("Error reading response:", err.Error())
		return
	}
	resp.Write(conn)
}

func (h *HTTP) servHTTPS(req *http.Request, conn net.Conn) {
	host := req.Host
	if !strings.Contains(host, ":") {
		host += ":443"
	}
	p, err := net.Dial("tcp", host)
	if err != nil {
		fmt.Println("Error connecting to", host, ":", err.Error())
		conn.Write([]byte("HTTP/1.1 502 Bad Gateway\r\n\r\n"))
		return
	}
	conn.Write([]byte("HTTP/1.1 200 OK\r\n\r\n"))
	go io.Copy(p, conn)
	io.Copy(conn, p)
}

func (h *HTTP) auth(req *http.Request) bool {
	auth := req.Header.Get("Proxy-Authorization")
	if auth == "" {
		return false
	}
	parts := strings.SplitN(auth, " ", 2)
	if len(parts) != 2 || parts[0] != "Basic" {
		return false
	}
	parts = strings.SplitN(parts[1], ":", 2)
	if len(parts) != 2 {
		return false
	}
	return parts[0] == h.user && parts[1] == h.pass
}
