func main() {
	listener, err := net.Listen("tcp", "0.0.0.0:1024")
	if err != nil {
		fmt.Println("Error listening:", err.Error())
		os.Exit(1)
	}
	defer listener.Close()
	fmt.Println("Listening on 0.0.0.0:1024")

	for {
		conn, err := listener.Accept()
		if err != nil {
			fmt.Println("Error accepting connection:", err.Error())
			os.Exit(1)
		}
		go handleRequest(conn)
	}
}

func handleRequest(conn net.Conn) {
	defer conn.Close()

	// Set IP TOS and TTL for IPv4 connections
	if conn.RemoteAddr().Network() == "tcp4" {
		ipConn, ok := conn.(*net.TCPConn)
		if !ok {
			fmt.Println("Error casting connection to TCPConn")
			return
		}
		file, err := ipConn.File()
		if err != nil {
			fmt.Println("Error getting file descriptor:", err.Error())
			return
		}
		defer file.Close()

		fd := int(file.Fd())
		err = syscall.SetsockoptInt(fd, syscall.IPPROTO_IP, syscall.IP_TOS, 0x10)
		if err != nil {
			fmt.Println("Error setting IP_TOS:", err.Error())
			return
		}
		err = syscall.SetsockoptInt(fd, syscall.IPPROTO_IP, syscall.IP_TTL, 64)
		if err != nil {
			fmt.Println("Error setting IP_TTL:", err.Error())
			return
		}
	}

	// Send greeting message
	_, err = conn.Write([]byte("HELLO-R-U-THERE-ACK"))
	if err != nil {
		fmt.Println("Error sending greeting message:", err.Error())
