package server

import (
	"context"
	"net"
	"sync"
	"time"
)

type Server struct {
	Addr         string
	Net          string
	Handler      Handler
	ReadTimeout  time.Duration
	WriteTimeout time.Duration
	IdleTimeout  func() time.Duration
	lock         sync.RWMutex
	started      bool
	shutdown     chan struct{}
	conns        map[net.Conn]struct{}
}

func (s *Server) ListenAndServe() error {
	if s.isStarted() {
		return nil
	}
	s.lock.Lock()
	s.started = true
	s.shutdown = make(chan struct{})
	s.conns = make(map[net.Conn]struct{})
	s.lock.Unlock()

	listener, err := net.Listen(s.Net, s.Addr)
	if err != nil {
		return err
	}
	defer listener.Close()

	go s.serveTCP(listener)

	<-s.shutdown

	return nil
}

func (s *Server) serveTCP(listener net.Listener) {
	for {
		conn, err := listener.Accept()
		if err != nil {
			select {
			case <-s.shutdown:
				return
			default:
				continue
			}
		}

		s.lock.Lock()
		s.conns[conn] = struct{}{}
		s.lock.Unlock()

		var wg sync.WaitGroup
		wg.Add(1)
		go s.serveTCPConn(&wg, conn)
		wg.Wait()

		s.lock.Lock()
		delete(s.conns, conn)
		s.lock.Unlock()
	}
}

func (s *Server) serveTCPConn(wg *sync.WaitGroup, conn net.Conn) {
	defer wg.Done()
	defer conn.Close()

	if s.ReadTimeout > 0 {
		conn.SetReadDeadline(time.Now().Add(s.ReadTimeout))
	}
	if s.WriteTimeout > 0 {
		conn.SetWriteDeadline(time.Now().Add(s.WriteTimeout))
	}

	s.Handler.ServeTCP(conn)

	if s.IdleTimeout != nil {
		conn.SetReadDeadline(time.Now().Add(s.IdleTimeout()))
	}
}

func (s *Server) ShutdownContext(ctx context.Context) error {
	if !s.isStarted() {
		return nil
	}
	s.lock.Lock()
	defer s.lock.Unlock()

	close(s.shutdown)

	for conn := range s.conns {
		conn.Close()
	}

	<-ctx.Done()

	return ctx.Err()
}

func (s *Server) isStarted() bool {
	s.lock.RLock()
	defer s.lock.RUnlock()
	return s.started
}
