package main

import (
	"fmt"
	"net"
	"os"
	"syscall"
)

func main() {
	listener, err := net.Listen("tcp", "[::]:1024")
	if err != nil {
		fmt.Println("Error listening:", err.Error())
		os.Exit(1)
	}
	defer listener.Close()

	fmt.Println("Listening on [::]:1024")

	for {
		conn, err := listener.Accept()
		if err != nil {
			fmt.Println("Error accepting connection:", err.Error())
			os.Exit(1)
		}

		go handleConnection(conn)
	}
}

func handleConnection(conn net.Conn) {
	defer conn.Close()

	if conn.RemoteAddr().Network() == "tcp6" {
		ipConn, ok := conn.(*net.TCPConn)
		if !ok {
			fmt.Println("Error casting connection to TCPConn")
			return
		}

		ipAddr := ipConn.RemoteAddr().(*net.TCPAddr).IP
		if ipAddr.To4() == nil {
			// IPv6 connection
			if err := setIPv6Params(ipConn); err != nil {
				fmt.Println("Error setting IPv6 parameters:", err.Error())
				return
			}
		}
	}

	_, err := conn.Write([]byte("Acknowledgment"))
	if err != nil {
		fmt.Println("Error sending acknowledgment:", err.Error())
		return
	}
}

func setIPv6Params(conn *net.TCPConn) error {
	ipConn, ok := conn.(*net.TCPConn)
	if !ok {
		return fmt.Errorf("error casting connection to TCPConn")
	}

	file, err := ipConn.File()
	if err != nil {
		return err
	}
	defer file.Close()

	fd := int(file.Fd())

	// Set traffic class
	if err := syscall.SetsockoptInt(fd, syscall.IPPROTO_IPV6, syscall.IPV6_TCLASS, 0x88); err != nil {
		return err
	}

	// Set hop limit
	if err := syscall.SetsockoptInt(fd, syscall.IPPROTO_IPV6, syscall.IPV6_UNICAST_HOPS, 64); err != nil {
		return err
	}

	return nil
}
