package rancher

import (
	"bytes"
	"encoding/json"
	"errors"
	"fmt"
	"io/ioutil"
	"net/http"
	"time"
)

type Client struct {
	url        string
	httpClient *http.Client
}

func NewRancherClient(backendNodes []string) (*Client, error) {
	client := &Client{
		url:        "http://rancher-metadata",
		httpClient: &http.Client{},
	}

	if len(backendNodes) > 0 {
		client.url = fmt.Sprintf("http://%s", backendNodes[0])
	}

	err := client.testConnection()
	if err != nil {
		return nil, err
	}

	return client, nil
}

func (c *Client) makeMetaDataRequest(path string) ([]byte, error) {
	req, err := http.NewRequest("GET", fmt.Sprintf("%s/%s", c.url, path), nil)
	if err != nil {
		return nil, err
	}

	req.Header.Set("Accept", "application/json")

	resp, err := c.httpClient.Do(req)
	if err != nil {
		return nil, err
	}
	defer resp.Body.Close()

	body, err := ioutil.ReadAll(resp.Body)
	if err != nil {
		return nil, err
	}

	if resp.StatusCode != http.StatusOK {
		return nil, errors.New(string(body))
	}

	return body, nil
}

func (c *Client) testConnection() error {
	backoff := 1 * time.Second
	maxBackoff := 20 * time.Second

	for {
		_, err := c.makeMetaDataRequest("ping")
		if err == nil {
			return nil
		}

		if backoff >= maxBackoff {
			return err
		}

		time.Sleep(backoff)
		backoff *= 2
	}
}
