		payload["vectors"][i] = map[string]interface{}{
			"id":     chunks[i].ID,
			"values": embedding,
			"metadata": map[string]interface{}{
				"chunk_id": chunks[i].ID,
				"source":   chunks[i].Source,
			},
		}
	}
	jsonPayload, err := json.Marshal(payload)
	if err != nil {
		return err
	}
	req, err := http.NewRequest("POST", url, bytes.NewBuffer(jsonPayload))
	if err != nil {
		return err
	}
	req.Header.Set("Api-Key", p.ApiKey)
	req.Header.Set("Content-Type", "application/json")
	client := &http.Client{}
	resp, err := client.Do(req)
	if err != nil {
		return err
	}
	defer resp.Body.Close()
	_, err = ioutil.ReadAll(resp.Body)
	if err != nil {
		return err
	}
	return nil
}

func (p *Pinecone) Retrieve(questionEmbedding []float32, topK int, uuid string) ([]QueryMatch, error) {
	url := fmt.Sprintf("%s/namespace/%s/query", p.Endpoint, uuid)
	payload := map[string]interface{}{
		"vector": questionEmbedding,
		"topK":   topK,
	}
	jsonPayload, err := json.Marshal(payload)
	if err != nil {
		return nil, err
	}
	req, err := http.NewRequest("POST", url, bytes.NewBuffer(jsonPayload))
	if err != nil {
		return nil, err
	}
	req.Header.Set("Api-Key", p.ApiKey)
	req.Header.Set("Content-Type", "application/json")
	client := &http.Client{}
	resp, err := client.Do(req)
	if err != nil {
		return nil, err
	}
	defer resp.Body.Close()
	body, err := ioutil.ReadAll(resp.Body)
	if err != nil {
		return nil, err
	}
	var result map[string]interface{}
	err = json.Unmarshal(body, &result)
	if err != nil {
		return nil, err
	}
	var matches []QueryMatch
	for _, match := range result["matches"].([]interface{}) {
		matches = append(matches, QueryMatch{
			ID:     match.(map[string]interface{})["id"].(string),
			Score:  match.(map[string]interface{})["score"].(float64),
			Vector: match.(map[string]interface{})["vector"].([]float64),
		})
	}
	return matches, nil
}
