	"github.com/GoogleCloudPlatform/spanner-migration-tool/spanner-migration-tool/spanner/writer/executor/spannerproxy"
	"github.com/GoogleCloudPlatform/spanner-migration-tool/spanner-migration-tool/spanner/writer/executor/spannerproxy/spannerproxygrpc"
)

func main() {
	proxyPort := "9010"
	spannerPort := "9020"
	cert := "cert.pem"

	// Validate required configuration parameters
	if proxyPort == "" || spannerPort == "" || cert == "" {
		log.Fatalf("Missing required configuration parameters")
	}

	// Set up the network listener
	lis, err := net.Listen("tcp", fmt.Sprintf(":%s", proxyPort))
	if err != nil {
		log.Fatalf("Failed to listen: %v", err)
	}

	// Load TLS credentials
	creds, err := loadTLSCredentials(cert)
	if err != nil {
		log.Fatalf("Failed to load TLS credentials: %v", err)
	}

	// Create the gRPC server instance
	s := grpc.NewServer(grpc.Creds(creds))

	// Register services
	spannerproxygrpc.RegisterSpannerExecutorProxyServer(s, &spannerproxy.SpannerExecutorProxy{})
	grpc_health_v1.RegisterHealthServer(s, health.NewServer())

	// Start serving requests
	log.Printf("Starting gRPC server on port %s", proxyPort)
	if err := s.Serve(lis); err != nil {
		log.Fatalf("Failed to serve: %v", err)
	}
}

func loadTLSCredentials(certFile string) (credentials.TransportCredentials, error) {
	// Load server certificate
	cert, err := tls.LoadX509KeyPair(certFile, certFile)
	if err != nil {
		return nil, err
	}

	// Create a certificate pool from the certificate file
	certPool := x509.NewCertPool()
	ca, err := ioutil.ReadFile(certFile)
	if err != nil {
		return nil, err
	}
	if ok := certPool.AppendCertsFromPEM(ca); !ok {
		return nil, fmt.Errorf("failed to append certificate")
	}

	// Create the TLS credentials
	creds := credentials.NewTLS(&tls.Config{
		Certificates: []tls.Certificate{cert},
		ClientCAs:    certPool,
		ClientAuth:   tls.RequireAndVerifyClientCert,
	})

	return creds, nil
}
