package grpcserver

import (
	"context"
	"crypto/tls"
	"crypto/x509"
	"fmt"
	"io/ioutil"
	"net"
	"time"

	"github.com/rs/zerolog/log"
	"google.golang.org/grpc"
	"google.golang.org/grpc/credentials"
	"google.golang.org/grpc/credentials/alts"
	"google.golang.org/grpc/keepalive"
	"google.golang.org/grpc/reflection"
)

type GRPCServerConfig struct {
	Address         string
	Network         string
	TLSCertPath     string
	TLSKeyPath      string
	MaxConnAge      time.Duration
	Enabled         bool
	BufferSize      int
	ClientCAPath    string
	MaxWorkers      uint32
	flagPrefix      string
}

type completedGRPCServer struct {
	opts            []grpc.ServerOption
	listener        net.Listener
	svcRegistration func(*grpc.Server)
	listenFunc      func() error
	prestopFunc     func()
	stopFunc        func()
	dial            func(context.Context, ...grpc.DialOption) (*grpc.ClientConn, error)
	netDial         func(context.Context, string) (net.Conn, error)
	creds           credentials.TransportCredentials
	certWatcher     *certwatcher.CertWatcher
}

func (c *GRPCServerConfig) Complete(level zerolog.Level, svcRegistrationFn func(*grpc.Server), opts ...grpc.ServerOption) (RunnableGRPCServer, error) {
	if !c.Enabled {
		return nil, fmt.Errorf("server is not enabled")
	}

	listener, dial, netDial, err := c.listenerAndDialer()
	if err != nil {
		return nil, err
	}

	tlsOpts, certWatcher, err := c.tlsOpts()
	if err != nil {
		return nil, err
	}

	clientCreds, err := c.clientCreds()
	if err != nil {
		return nil, err
	}

	grpcOpts := append([]grpc.ServerOption{
		grpc.Creds(clientCreds),
		grpc.KeepaliveParams(keepalive.ServerParameters{
			Time:    c.MaxConnAge,
			Timeout: c.MaxConnAge,
		}),
		grpc.MaxConcurrentStreams(c.MaxWorkers),
	}, tlsOpts...)

	grpcOpts = append(grpcOpts, opts...)

	s := &completedGRPCServer{
		opts:            grpcOpts,
		listener:        listener,
		svcRegistration: svcRegistrationFn,
		dial:            dial,
		netDial:         netDial,
		creds:           clientCreds,
		certWatcher:     certWatcher,
	}

	return s, nil
}

func (c *GRPCServerConfig) listenerAndDialer() (net.Listener, DialFunc, NetDialFunc, error) {
	var listener net.Listener
	var dial DialFunc
	var netDial NetDialFunc
	var err error

	switch c.Network {
	case "tcp":
		listener, err = net.Listen("tcp", c.Address)
		if err != nil {
			return nil, nil, nil, err
		}
		dial = func(ctx context.Context, opts ...grpc.DialOption) (*grpc.ClientConn, error) {
			return grpc.DialContext(ctx, c.Address, opts...)
		}
		netDial = func(ctx context.Context, addr string) (net.Conn, error) {
			return net.DialContext(ctx, "tcp", addr)
		}
	case "unix":
		listener, err = net.Listen("unix", c.Address)
		if err != nil {
			return nil, nil, nil, err
		}
		dial = func(ctx context.Context, opts ...grpc.DialOption) (*grpc.ClientConn, error) {
			return grpc.DialContext(ctx, "unix://"+c.Address, opts...)
		}
		netDial = func(ctx context.Context, addr string) (net.Conn, error) {
			return net.DialContext(ctx, "unix", addr)
		}
	case "buffered":
		listener = &bufferedListener{
			buffer: make([]byte, c.BufferSize),
			addr:   c.Address,
		}
		dial = func(ctx context.Context, opts ...grpc.DialOption) (*grpc.ClientConn, error) {
			return grpc.DialContext(ctx, "buffered://"+c.Address, opts...)
		}
		netDial = func(ctx context.Context, addr string) (net.Conn, error) {
			return net.DialContext(ctx, "buffered", addr)
		}
	default:
		return nil, nil, nil, fmt.Errorf("unsupported network type: %s", c.Network)
	}

	return listener, dial, netDial, nil
}

func (c *GRPCServerConfig) tlsOpts() ([]grpc.ServerOption, *certwatcher.CertWatcher, error) {
	if c.TLSCertPath == "" || c.TLSKeyPath == "" {
		return nil, nil, nil
	}

	cert, err := tls.LoadX509KeyPair(c.TLSCertPath, c.TLSKeyPath)
	if err != nil {
		return nil, nil, err
	}

	certWatcher, err := certwatcher.New(c.TLSCertPath, c.TLSKeyPath)
	if err != nil {
		return nil, nil, err
	}

	tlsConfig := &tls.Config{
		Certificates: []tls.Certificate{cert},
		ClientAuth:   tls.RequireAndVerifyClientCert,
		ClientCAs:    nil,
	}

	if c.ClientCAPath != "" {
		caCert, err := ioutil.ReadFile(c.ClientCAPath)
		if err != nil {
			return nil, nil, err
		}

		caCertPool := x509.NewCertPool()
		caCertPool.AppendCertsFromPEM(caCert)

		tlsConfig.ClientCAs = caCertPool
	}

	return []grpc.ServerOption{
		grpc.Creds(credentials.NewTLS(tlsConfig)),
	}, certWatcher, nil
}

func (c *GRPCServerConfig) clientCreds() (credentials.TransportCredentials, error) {
	if c.TLSCertPath == "" || c.TLSKeyPath == "" {
		return nil, nil
	}

	cert, err := tls.LoadX509KeyPair(c.TLSCertPath, c.TLSKeyPath)
	if err != nil {
		return nil, err
	}

	tlsConfig := &tls.Config{
		Certificates: []tls.Certificate{cert},
	}

	if c.ClientCAPath != "" {
		caCert, err := ioutil.ReadFile(c.ClientCAPath)
		if err != nil {
			return nil, err
		}

		caCertPool := x509.NewCertPool()
		caCertPool.AppendCertsFromPEM(caCert)

		tlsConfig.RootCAs = caCertPool
	}

	return credentials.NewTLS(tlsConfig), nil
}

func (s *completedGRPCServer) WithOpts(opts ...grpc.ServerOption) RunnableGRPCServer {
	s.opts = append(s.opts, opts...)
	return s
}

func (s *completedGRPCServer) Listen(ctx context.Context) func() error {
	s.listenFunc = func() error {
		server := grpc.NewServer(s.opts...)
		s.svcRegistration(server)
		reflection.Register(server)

		log.Info().Str("address", s.listener.Addr().String()).Msg("gRPC server listening")

		go func() {
			<-ctx.Done()
			s.stopFunc()
		}()

		return server.Serve(s.listener)
	}

	return s.listenFunc
}

func (s *completedGRPCServer) DialContext(ctx context.Context, opts ...grpc.DialOption) (*grpc.ClientConn, error) {
	return grpc.DialContext(ctx, s.listener.Addr().String(), opts...)
}

func (s *completedGRPCServer) NetDialContext(ctx context.Context, addr string) (net.Conn, error) {
	return net.DialContext(ctx, s.listener.Addr().Network(), addr)
}

func (s *completedGRPCServer) Insecure() bool {
	return s.creds == nil
}

func (s *completedGRPCServer) GracefulStop() {
	s.stopFunc()
}

type RunnableGRPCServer interface {
	WithOpts(...grpc.ServerOption) RunnableGRPCServer
	Listen(context.Context) func() error
	DialContext(context.Context, ...grpc.DialOption) (*grpc.ClientConn, error)
	NetDialContext(context.Context, string) (net.Conn, error)
	Insecure() bool
	GracefulStop()
}

type DialFunc func(context.Context, ...grpc.DialOption) (*grpc.ClientConn, error)

type NetDialFunc func(context.Context, string) (net.Conn, error)
