			fmt.Println("Failed to send Async message:", err)
		} else {
			fmt.Println("Async message sent successfully:", result)
		}
	})
	if err != nil {
		fmt.Println("Failed to send Async message:", err)
	}

	// Send Delay Sync message
	result, err = producer.sendDelaySyncMessage(ctx, topic, body, tag, keys, delayLevel)
	if err != nil {
