package main

import (
	"bytes"
	"errors"
	"io/ioutil"
	"net/http"
	"sync"
)

var fileCache = make(map[string][]byte)
var fileCacheMutex sync.RWMutex
var fileCacheEnable = true
var verboseReader = true

func fetchFile(fileurl string) ([]byte, error) {
	if fileCacheEnable {
		fileCacheMutex.RLock()
		if cached, ok := fileCache[fileurl]; ok {
			fileCacheMutex.RUnlock()
			return cached, nil
		}
		fileCacheMutex.RUnlock()
	}

	resp, err := http.Get(fileurl)
	if err != nil {
		return nil, err
	}
	defer resp.Body.Close()

	if resp.StatusCode != http.StatusOK {
		return nil, errors.New("non-200 status code: " + resp.Status)
	}

	body, err := ioutil.ReadAll(resp.Body)
	if err != nil {
		return nil, err
	}

	if fileCacheEnable {
		fileCacheMutex.Lock()
		fileCache[fileurl] = body
		fileCacheMutex.Unlock()
	}

	return body, nil
}
