	"io/ioutil"
	"net/http"
	"os"
	"path/filepath"
)

type dlFile struct {
	name string
	url  string
	filt func([]byte) []byte
	dirs []string
}

func dl(f dlFile) {
	resp, err := http.Get(f.url)
	if err != nil {
		fmt.Printf("Sync %s: failed to download\n", f.name)
		return
	}
	defer resp.Body.Close()

	if resp.StatusCode != http.StatusOK {
		fmt.Printf("Sync %s: failed to download (status code: %d)\n", f.name, resp.StatusCode)
		return
	}

	body, err := ioutil.ReadAll(resp.Body)
	if err != nil {
		fmt.Printf("Sync %s: failed to read response body\n", f.name)
		return
	}

	if f.filt != nil {
		body = f.filt(body)
	}

	for _, dir := range f.dirs {
		err := os.MkdirAll(dir, os.ModePerm)
		if err != nil {
			fmt.Printf("Sync %s: failed to create directory %s\n", f.name, dir)
			continue
		}

		path := filepath.Join(dir, f.name)
		err = ioutil.WriteFile(path, body, 0644)
		if err != nil {
			fmt.Printf("Sync %s: failed to write to %s\n", f.name, path)
			continue
		}

		fmt.Printf("Sync %s: ok\n", f.name)
	}
}
