func getNode(id string) (string, error) {
	// Start an HTTP server
	server := &http.Server{Addr: ":8080"}

	// Define a handler function for the GET request
	http.HandleFunc("/node", func(w http.ResponseWriter, r *http.Request) {
		// Check if the request method is GET
		if r.Method != http.MethodGet {
			http.Error(w, "Method not allowed", http.StatusMethodNotAllowed)
			return
		}

		// Retrieve the node information based on the provided ID
		nodeInfo := fmt.Sprintf("Node information for ID: %s", id)

		// Write the node information to the response body
		fmt.Fprint(w, nodeInfo)
	})

	// Start the server in a separate goroutine
	go func() {
		if err := server.ListenAndServe(); err != nil && err != http.ErrServerClosed {
			fmt.Printf("Server error: %v\n", err)
		}
	}()

	// Wait for the server to start
	for {
		_, err := http.Get("http://localhost:8080/node")
		if err == nil {
			break
		}
	}

	// Send a GET request to retrieve the node information
	resp, err := http.Get("http://localhost:8080/node")
	if err != nil {
		return "", err
	}
	defer resp.Body.Close()

	// Read the response body
	body, err := ioutil.ReadAll(resp.Body)
	if err != nil {
		return "", err
	}

	// Stop the server
	if err := server.Shutdown(nil); err != nil {
		fmt.Printf("Server shutdown error: %v\n", err)
	}

	// Return the response body
	return string(body), nil
}

func main() {
	// Example usage
	nodeInfo, err := getNode("123")
	if err != nil {
		fmt.Printf("Error: %v\n", err)
		os.Exit(1)
	}

	fmt.Println(nodeInfo)
}
