	if cached, ok := cache[fileurl]; ok {
		cacheMutex.Unlock()
		return cached, nil
	}
	cacheMutex.Unlock()

	resp, err := http.Get(fileurl)
	if err != nil {
		return nil, err
	}
	defer resp.Body.Close()

	if resp.StatusCode != http.StatusOK {
		return nil, errors.New("non-200 HTTP status")
	}

	body, err := ioutil.ReadAll(resp.Body)
	if err != nil {
		return nil, err
	}

	cacheMutex.Lock()
	cache[fileurl] = body
	cacheMutex.Unlock()

	return body, nil
}
