)

type WorkWeiXin struct {
	CorpID     string
	AgentID    string
	CorpSecret string
	Token      string
}

func (ww *WorkWeiXin) getToken() (string, error) {
	if ww.Token != "" && time.Now().Before(ww.TokenExpiresAt) {
		return ww.Token, nil
	}

	url := fmt.Sprintf("https://qyapi.weixin.qq.com/cgi-bin/gettoken?corpid=%s&corpsecret=%s", ww.CorpID, ww.CorpSecret)
	resp, err := http.Get(url)
	if err != nil {
		return "", err
	}
	defer resp.Body.Close()

	body, err := ioutil.ReadAll(resp.Body)
	if err != nil {
		return "", err
	}

	var result struct {
		ErrCode     int    `json:"errcode"`
		ErrMsg      string `json:"errmsg"`
		AccessToken string `json:"access_token"`
		ExpiresIn   int    `json:"expires_in"`
	}
	err = json.Unmarshal(body, &result)
	if err != nil {
		return "", err
	}

	if result.ErrCode != 0 {
		return "", errors.New(result.ErrMsg)
	}

	ww.Token = result.AccessToken
	ww.TokenExpiresAt = time.Now().Add(time.Duration(result.ExpiresIn) * time.Second)

	return ww.Token, nil
}

func (ww *WorkWeiXin) Send(receivers []string, title string, content []byte, others ...string) error {
	token, err := ww.getToken()
	if err != nil {
		return err
	}

	url := fmt.Sprintf("https://qyapi.weixin.qq.com/cgi-bin/message/send?access_token=%s", token)
	data := map[string]interface{}{
		"touser":  receivers,
		"msgtype": "markdown",
		"agentid": ww.AgentID,
		"markdown": map[string]string{
			"content": fmt.Sprintf("## %s\n%s", title, string(content)),
		},
	}
	for _, other := range others {
		data[other] = true
	}

	jsonData, err := json.Marshal(data)
	if err != nil {
		return err
	}

	resp, err := http.Post(url, "application/json", bytes.NewBuffer(jsonData))
	if err != nil {
		return err
	}
	defer resp.Body.Close()

	body, err := ioutil.ReadAll(resp.Body)
	if err != nil {
		return err
	}

	var result struct {
		ErrCode int    `json:"errcode"`
		ErrMsg  string `json:"errmsg"`
	}
	err = json.Unmarshal(body, &result)
	if err != nil {
		return err
	}

	if result.ErrCode != 0 {
		return errors.New(result.ErrMsg)
	}

	return nil
}
