package main

import (
	"fmt"
	"log"
	"os"
	"os/signal"
	"syscall"

	"github.com/streadway/amqp"
)

type Consumer struct {
	conn    *amqp.Connection
	channel *amqp.Channel
	tag     string
	done    chan error
}

func NewConsumer(amqpURI, exchange, exchangeType, queueName, key, ctag string) (*Consumer, error) {
	c := &Consumer{
		done: make(chan error),
	}

	var err error

	// Connect to RabbitMQ
	c.conn, err = amqp.Dial(amqpURI)
	if err != nil {
		return nil, fmt.Errorf("Dial: %s", err)
	}

	// Create a channel
	c.channel, err = c.conn.Channel()
	if err != nil {
		return nil, fmt.Errorf("Channel: %s", err)
	}

	// Declare an exchange
	if err = c.channel.ExchangeDeclare(
		exchange,     // name
		exchangeType, // type
		true,         // durable
		false,        // auto-deleted
		false,        // internal
		false,        // no-wait
		nil,          // arguments
	); err != nil {
		return nil, fmt.Errorf("Exchange Declare: %s", err)
	}

	// Declare a queue
	q, err := c.channel.QueueDeclare(
		queueName, // name
		true,      // durable
		false,     // delete when unused
		false,     // exclusive
		false,     // no-wait
		nil,       // arguments
	)
	if err != nil {
		return nil, fmt.Errorf("Queue Declare: %s", err)
	}

	// Bind the queue
	if err = c.channel.QueueBind(
		q.Name,   // queue name
		key,      // routing key
		exchange, // exchange
		false,    // no-wait
		nil,      // arguments
	); err != nil {
		return nil, fmt.Errorf("Queue Bind: %s", err)
	}

	// Register a consumer
	deliveries, err := c.channel.Consume(
		q.Name, // queue
		ctag,   // consumer
		false,  // auto-ack
		false,  // exclusive
		false,  // no-local
		false,  // no-wait
		nil,    // arguments
	)
	if err != nil {
		return nil, fmt.Errorf("Queue Consume: %s", err)
	}

	// Start consuming messages
	go c.handle(deliveries, c.done)

	return c, nil
}

func (c *Consumer) Shutdown() error {
	// Send a signal to the consumer to shut down
	c.done <- nil

	// Cancel the consumer
	if err := c.channel.Cancel(c.tag, true); err != nil {
		return fmt.Errorf("Consumer cancel failed: %s", err)
	}

	// Close the channel
	if err := c.channel.Close(); err != nil {
		return fmt.Errorf("Channel close failed: %s", err)
	}

	// Close the connection
	if err := c.conn.Close(); err != nil {
		return fmt.Errorf("AMQP connection close failed: %s", err)
	}

	return nil
}

func (c *Consumer) handle(deliveries <-chan amqp.Delivery, done chan error) {
	for d := range deliveries {
		// Process the message
		log.Printf("Received a message: %s", d.Body)

		// Acknowledge the message
		d.Ack(false)
	}

	// Signal that the consumer has finished
	done <- nil
}

func main() {
	// RabbitMQ connection parameters
	amqpURI := "amqp://guest:guest@localhost:5672/"
	exchange := "test-exchange"
	exchangeType := "direct"
	queueName := "test-queue"
	key := "test-key"
	ctag := "test-consumer"

	// Create a new consumer
	consumer, err := NewConsumer(amqpURI, exchange, exchangeType, queueName, key, ctag)
	if err != nil {
		log.Fatalf("%s", err)
	}

	// Register a signal handler for graceful shutdown
	signals := make(chan os.Signal, 1)
	signal.Notify(signals, os.Interrupt, syscall.SIGTERM)

	// Wait for a signal or an error
	select {
	case <-signals:
		log.Println("Interrupt received. Shutting down...")
	case err := <-consumer.done:
		if err != nil {
			log.Fatalf("Consumer error: %s", err)
		}
	}

	// Shutdown the consumer
	if err := consumer.Shutdown(); err != nil {
		log.Fatalf("Consumer shutdown error: %s", err)
	}
}
