package main

import (
	"log"
	"time"

	"github.com/streadway/amqp"
)

type Session struct {
	name           string
	logger         *log.Logger
	connection     *amqp.Connection
	channel        *amqp.Channel
	done           chan bool
	notifyConnClose chan *amqp.Error
	notifyChanClose chan *amqp.Error
	notifyConfirm  chan amqp.Confirmation
	isReady        bool
}

func New(name string, addr string) *Session {
	s := &Session{
		name:           name,
		logger:         log.Default(),
		done:           make(chan bool),
		notifyConnClose: make(chan *amqp.Error, 1),
		notifyChanClose: make(chan *amqp.Error, 1),
		notifyConfirm:  make(chan amqp.Confirmation, 1),
	}

	go s.handleReconnect(addr)

	return s
}

func (s *Session) handleReconnect(addr string) {
	for {
		select {
		case <-s.done:
			return
		default:
			conn, err := s.connect(addr)
			if err != nil {
				s.logger.Printf("Failed to connect to RabbitMQ: %v", err)
				time.Sleep(5 * time.Second)
				continue
			}

			s.changeConnection(conn)
			if s.handleReInit(conn) {
				s.logger.Println("Connection re-established and channel initialized.")
			} else {
				s.logger.Println("Connection re-established but failed to initialize channel.")
			}
		}
	}
}

func (s *Session) connect(addr string) (*amqp.Connection, error) {
	conn, err := amqp.Dial(addr)
	if err != nil {
		return nil, err
	}

	return conn, nil
}

func (s *Session) handleReInit(conn *amqp.Connection) bool {
	err := s.init(conn)
	if err != nil {
		return false
	}

	return true
}

func (s *Session) init(conn *amqp.Connection) error {
	ch, err := conn.Channel()
	if err != nil {
		return err
	}

	err = ch.Confirm(false)
	if err != nil {
		return err
	}

	err = ch.NotifyClose(s.notifyChanClose)
	if err != nil {
		return err
	}

	err = ch.NotifyPublish(s.notifyConfirm)
	if err != nil {
		return err
	}

	queue, err := ch.QueueDeclare(
		s.name, // name
		true,   // durable
		false,  // delete when unused
		false,  // exclusive
		false,  // no-wait
		nil,    // arguments
	)
	if err != nil {
		return err
	}

	s.channel = &ch
	s.isReady = true

	s.logger.Printf("Declared queue %s with %d messages and %d consumers", queue.Name, queue.Messages, queue.Consumers)

	return nil
}

func (s *Session) changeConnection(connection *amqp.Connection) {
	s.connection = connection
	s.connection.NotifyClose(s.notifyConnClose)
}

func (s *Session) changeChannel(channel *amqp.Channel) {
	s.channel = channel
	s.channel.NotifyClose(s.notifyChanClose)
	s.channel.NotifyPublish(s.notifyConfirm)
}

func (s *Session) Push(data []byte) error {
	if !s.isReady {
		return amqp.ErrClosed
	}

	for {
		err := s.UnsafePush(data)
		if err == nil {
			return nil
		}

		s.logger.Printf("Failed to push message: %v", err)
		time.Sleep(1 * time.Second)
	}
}

func (s *Session) UnsafePush(data []byte) error {
	if !s.isReady {
		return amqp.ErrClosed
	}

	err := s.channel.Publish(
		"",         // exchange
		s.name,     // routing key
		false,      // mandatory
		false,      // immediate
		amqp.Publishing{
			ContentType: "text/plain",
			Body:        data,
		},
	)

	return err
}
