		for {
			_, message, err := conn.ReadMessage()
			if err != nil {
				log.Println("Error reading message:", err)
				return
			}
			fmt.Println("Received message:", string(message))
			if string(message) == "auth_url" {
				_, _, err := conn.ReadMessage()
				if err != nil {
					log.Println("Error reading auth URL:", err)
					return
				}
				fmt.Println("Please open the following URL in your browser and enter the code:")
				fmt.Println(string(message))
			} else if string(message) == "jwt_token" {
				_, jwtToken, err := conn.ReadMessage()
				if err != nil {
					log.Println("Error reading JWT token:", err)
					return
				}
				fmt.Println("Authentication successful. JWT token received.")
				fmt.Println("JWT token:", string(jwtToken))
				return
			}
		}
	}()

	// Wait for interrupt signal to gracefully close the connection
	interrupt := make(chan os.Signal, 1)
	signal.Notify(interrupt, os.Interrupt, syscall.SIGTERM)

	select {
	case <-done:
		return string(done)
	case <-interrupt:
		log.Println("Interrupt signal received. Closing connection...")
	}

	return ""
}

