package main

import (
	"net/http"
	"net/url"
	"github.com/gorilla/websocket"
)

type WebsocketProxy struct {
	Director func(incoming *http.Request, out http.Header)
	Backend  func(*http.Request) *url.URL
	Upgrader *websocket.Upgrader
	Dialer   *websocket.Dialer
}

func (p *WebsocketProxy) ServeHTTP(rw http.ResponseWriter, req *http.Request) {
	backendURL := p.Backend(req)
	if backendURL == nil {
		http.Error(rw, "No backend URL", http.StatusInternalServerError)
		return
	}

	backendURL.Scheme = "ws"
	backendURL.Path = req.URL.Path
	backendURL.RawQuery = req.URL.RawQuery

	backendHeader := http.Header{}
	if p.Director != nil {
		p.Director(req, backendHeader)
	}

	backendConn, _, err := p.Dialer.Dial(backendURL.String(), backendHeader)
	if err != nil {
		http.Error(rw, "Failed to connect to backend", http.StatusBadGateway)
		return
	}
	defer backendConn.Close()

	clientConn, err := p.Upgrader.Upgrade(rw, req, nil)
	if err != nil {
		http.Error(rw, "Failed to upgrade client connection", http.StatusBadRequest)
		return
	}
	defer clientConn.Close()

	go func() {
		for {
			_, message, err := clientConn.ReadMessage()
			if err != nil {
				return
			}
			err = backendConn.WriteMessage(websocket.TextMessage, message)
			if err != nil {
				return
			}
		}
	}()

	for {
		_, message, err := backendConn.ReadMessage()
		if err != nil {
			return
		}
		err = clientConn.WriteMessage(websocket.TextMessage, message)
		if err != nil {
			return
		}
	}
}
