package main

import (
	"encoding/binary"
	"errors"
	"net"
	"strings"
)

func WakeUp(macAddr, broadcastIP, bcastInterface string, port int) error {
	if len(macAddr) != 17 || strings.Count(macAddr, ":") != 5 {
		return errors.New("invalid MAC address format")
	}

	macBytes, err := net.ParseMAC(macAddr)
	if err != nil {
		return err
	}

	packet := make([]byte, 102)
	for i := 0; i < 6; i++ {
		packet[i] = 0xFF
	}
	for i := 6; i < len(packet); i += 6 {
		copy(packet[i:i+6], macBytes)
	}

	addr, err := net.ResolveUDPAddr("udp", net.JoinHostPort(broadcastIP, strconv.Itoa(port)))
	if err != nil {
		return err
	}

	conn, err := net.DialUDP("udp", nil, addr)
	if err != nil {
		return err
	}
	defer conn.Close()

	if bcastInterface != "" {
		iface, err := net.InterfaceByName(bcastInterface)
		if err != nil {
			return err
		}
		err = conn.SetWriteDeadline(time.Now().Add(3 * time.Second))
		if err != nil {
			return err
		}
		err = conn.SetWriteBuffer(len(packet))
		if err != nil {
			return err
		}
		err = conn.SetNetworkInterface(iface)
		if err != nil {
			return err
		}
	}

	_, err = conn.Write(packet)
	if err != nil {
		return err
	}

	return nil
}
