package dns

import (
	"context"
	"net"
	"sync"
	"sync/atomic"
	"time"

	"github.com/miekg/dns"
	"go.uber.org/zap"
	"golang.org/x/exp/slices"
	"golang.org/x/net/netip"
)

type Bootstrap struct {
	fqdn     string
	port     uint16
	bootstrap *net.UDPAddr
	qt       uint16
	logger   *zap.Logger
	updating atomic.Bool
	nextUpdate time.Time
	readyNotify chan struct{}
	m         sync.Mutex
	ready     bool
	addrStr   string
}

func (b *Bootstrap) resolve(ctx context.Context, qt uint16) (netip.Addr, uint32, error) {
	if b.updating.Load() {
		<-b.readyNotify
	}

	b.m.Lock()
	if b.ready {
		b.m.Unlock()
		addr, err := netip.ParseAddr(b.addrStr)
		if err != nil {
			return netip.Addr{}, 0, err
		}
		return addr, 0, nil
	}
	b.m.Unlock()

	b.updating.Store(true)
	defer b.updating.Store(false)

	var (
		conn *net.UDPConn
		err  error
	)

	for i := 0; i < 3; i++ {
		conn, err = net.DialUDP("udp", nil, b.bootstrap)
		if err == nil {
			break
		}
		time.Sleep(100 * time.Millisecond)
	}

	if err != nil {
		return netip.Addr{}, 0, err
	}
	defer conn.Close()

	m := new(dns.Msg)
	m.SetQuestion(dns.Fqdn(b.fqdn), qt)
	m.RecursionDesired = true

	err = conn.SetDeadline(time.Now().Add(5 * time.Second))
	if err != nil {
		return netip.Addr{}, 0, err
	}

	_, err = conn.Write(m.Bytes())
	if err != nil {
		return netip.Addr{}, 0, err
	}

	buf := make([]byte, 1500)
	n, _, err := conn.ReadFromUDP(buf)
	if err != nil {
		return netip.Addr{}, 0, err
	}

	resp := new(dns.Msg)
	err = resp.Unpack(buf[:n])
	if err != nil {
		return netip.Addr{}, 0, err
	}

	if resp.Rcode != dns.RcodeSuccess {
		return netip.Addr{}, 0, dns.RcodeError(resp.Rcode)
	}

	var (
		addr netip.Addr
		ttl  uint32
	)

	for _, a := range resp.Answer {
		switch rr := a.(type) {
		case *dns.A:
			addr = netip.AddrFrom4(rr.A)
			ttl = uint32(rr.Hdr.Ttl)
		case *dns.AAAA:
			addr = netip.AddrFrom16(rr.AAAA)
			ttl = uint32(rr.Hdr.Ttl)
		}
	}

	if addr.IsValid() {
		b.m.Lock()
		b.ready = true
		b.addrStr = addr.String()
		b.m.Unlock()
		close(b.readyNotify)
	}

	return addr, ttl, nil
}
