package derpserver

import (
	"net/http"

	"github.com/tailscale/tailscale/net/derp"
	"nhooyr.io/websocket"
)

type DERPServer struct {
	key           key.NodePrivate
	tailscaleDERP *derp.Server
}

func (s *DERPServer) serveWebsocket(writer http.ResponseWriter, req *http.Request) {
	conn, err := websocket.Accept(writer, req, &websocket.AcceptOptions{
		Subprotocols: []string{"tailscale-derp"},
	})
	if err != nil {
		return
	}
	defer conn.Close(websocket.StatusInternalError, "internal error")

	if conn.Subprotocol() != "tailscale-derp" {
		conn.Close(websocket.StatusPolicyViolation, "invalid subprotocol")
		return
	}

	networkConn := &wsNetworkConn{conn: conn}
	s.tailscaleDERP.HandleConn(networkConn)
}

type wsNetworkConn struct {
	conn *websocket.Conn
}

func (c *wsNetworkConn) Read(p []byte) (int, error) {
	_, r, err := c.conn.Read(context.Background())
	if err != nil {
		return 0, err
	}
	return r.Read(p)
}

func (c *wsNetworkConn) Write(p []byte) (int, error) {
	err := c.conn.Write(context.Background(), websocket.MessageBinary, p)
	if err != nil {
		return 0, err
	}
	return len(p), nil
}

func (c *wsNetworkConn) Close() error {
	return c.conn.Close(websocket.StatusNormalClosure, "")
}
