package main

import (
	"bytes"
	"context"
	"fmt"
	"io"
	"os"
	"os/exec"
	"syscall"
)

func copyViaOsascript(ctx context.Context, password string) error {
	// Create a pipe for the command's standard input
	pipeReader, pipeWriter, err := os.Pipe()
	if err != nil {
		return fmt.Errorf("failed to create pipe: %w", err)
	}
	defer pipeWriter.Close()

	// Start the osascript command with a pipe for standard input
	cmd := exec.CommandContext(ctx, "osascript", "-e", `on run {in}
		set the clipboard to in
	end run`, password)
	cmd.Stdin = pipeReader
	cmd.Stdout = os.Stdout
	cmd.Stderr = os.Stderr

	// Start the command
	if err := cmd.Start(); err != nil {
		return fmt.Errorf("failed to start osascript command: %w", err)
	}

	// Close the pipe writer to signal osascript that it can read the password
	if err := pipeWriter.Close(); err != nil {
		return fmt.Errorf("failed to close pipe writer: %w", err)
	}

	// Wait for the command to finish
	if err := cmd.Wait(); err != nil {
		return fmt.Errorf("osascript command failed: %w", err)
	}

	return nil
}

func main() {
	// Example usage
	ctx := context.Background()
	password := "examplePassword"
	if err := copyViaOsascript(ctx, password); err != nil {
		fmt.Println("Error:", err)
	} else {
		fmt.Println("Password copied to clipboard successfully.")
	}
}
