package main

import (
	"fmt"
	"log"
	"net/http"
	"sync"
	"time"
)

var (
	response   http.Response
	responses  = make(map[string]http.Response)
	responsesM sync.Mutex
)

func main() {
	http.HandleFunc("/", func(w http.ResponseWriter, r *http.Request) {
		proxify := r.Header.Get("proxify")
		if proxify == "" {
			http.Error(w, "Missing proxify header", http.StatusBadRequest)
			return
		}

		responsesM.Lock()
		resp, ok := responses[proxify]
		responsesM.Unlock()

		if !ok {
			http.Error(w, "No response found for proxify header", http.StatusNotFound)
			return
		}

		defer func() {
			responsesM.Lock()
			delete(responses, proxify)
			responsesM.Unlock()
		}()

		for k, v := range resp.Header {
			w.Header()[k] = v
		}
		w.WriteHeader(resp.StatusCode)
		w.Write(resp.Body)
	})

	go func() {
		log.Fatal(http.ListenAndServe(options.HTTPListenerAddress, nil))
	}()

	fmt.Printf("Server is running on %s\n", options.HTTPListenerAddress)
	select {}
}
