func main() {
	http.HandleFunc("/", func(w http.ResponseWriter, r *http.Request) {
		cookie, err := r.Cookie("logged-in")
		if err == http.ErrNoCookie {
			// Initial visit
			http.SetCookie(w, &http.Cookie{Name: "logged-in", Value: "false"})
			fmt.Fprint(w, "You are not logged in. Please log in.")
			return
		}

		if r.Method == "POST" {
			// Successful login
			if cookie.Value == "false" {
				http.SetCookie(w, &http.Cookie{Name: "logged-in", Value: "true"})
				fmt.Fprint(w, "You are now logged in.")
				return
			}
		}

		if r.URL.Path == "/logout" {
			// Logout request
			http.SetCookie(w, &http.Cookie{Name: "logged-in", Value: "false"})
			fmt.Fprint(w, "You are now logged out.")
			return
		}

		// User is logged in
		fmt.Fprint(w, "You are logged in.")
	})

	http.ListenAndServe(":8080", nil)
}
