package main

import (
	"log"
	"net/http"
	"time"

	"github.com/golang/glog"
	"github.com/rs/cors"
	"golang.org/x/net/webdav"
)

type webdavHandler struct {
	FileSystem webdav.FileSystem
	LockSystem webdav.LockSystem
	Logger     func(*http.Request, error)
}

func (h *webdavHandler) ServeHTTP(w http.ResponseWriter, r *http.Request) {
	start := time.Now()
	err := h.FileSystem.ServeHTTP(w, r)
	h.Logger(r, err)
	glog.Infof("%s %s %v", r.Method, r.URL.Path, time.Since(start))
}

func (h *webdavHandler) NewMemFS() webdav.FileSystem {
	return webdav.NewMemFS()
}

func (h *webdavHandler) NewMemLS() webdav.LockSystem {
	return webdav.NewMemLS()
}

func main() {
	handler := &webdavHandler{
		FileSystem: webdav.NewMemFS(),
		LockSystem: webdav.NewMemLS(),
		Logger: func(r *http.Request, err error) {
			if err != nil {
				glog.Errorf("%s %s: %v", r.Method, r.URL.Path, err)
			} else {
				glog.Infof("%s %s", r.Method, r.URL.Path)
			}
		},
	}

	mux := http.NewServeMux()
	mux.Handle("/", http.HandlerFunc(requestHandler(handler)))

	c := cors.New(cors.Options{
		AllowedOrigins:   []string{"*"},
		AllowedMethods:   []string{"GET", "POST", "PUT", "DELETE", "OPTIONS"},
		AllowedHeaders:   []string{"Authorization", "Content-Type"},
		AllowCredentials: true,
	})

	handler := c.Handler(mux)

	log.Fatal(http.ListenAndServe(":8080", handler))
}

func requestHandler(handler *webdavHandler) http.HandlerFunc {
	return func(w http.ResponseWriter, r *http.Request) {
		if r.Header.Get("X-Special-Header") == "true" {
			// Handle special header
			// ...
		}
		handler.ServeHTTP(w, r)
	}
}
