package main

import (
	"flag"
	"log"
	"net/http"

	"golang.org/x/net/webdav"
)

func main() {
	port := flag.String("port", "8080", "Port to listen on")
	flag.Parse()

	memFS := webdav.NewMemFS()
	memLS := webdav.NewMemLS()
	handler := webdav.Handler{
		FileSystem: memFS,
		LockSystem: memLS,
		Logger: func(r *http.Request, err error) {
			if err != nil {
				log.Printf("ERROR: %s %s: %v", r.Method, r.URL.Path, err)
			} else {
				log.Printf("INFO: %s %s", r.Method, r.URL.Path)
			}
		},
	}

	http.Handle("/", handler)

	// Special handling for Litmus test case "props: 3 (propfind_invalid2)"
	http.HandleFunc("/litmus/props/3/propfind_invalid2", func(w http.ResponseWriter, r *http.Request) {
		if r.Method == http.MethodPropfind {
			w.WriteHeader(http.StatusBadRequest)
			return
		}
		handler.ServeHTTP(w, r)
	})

	log.Printf("Listening on port %s", *port)
	log.Fatal(http.ListenAndServe(":"+*port, nil))
}
