package main

import (
	"fmt"
	"net/http"
	"os"
	"strings"

	"github.com/labstack/echo/v4"
	"github.com/labstack/echo/v4/middleware"
)

func main() {
	e := echo.New()

	// Middleware
	e.Use(middleware.Logger())
	e.Use(middleware.Recover())
	e.Use(middleware.Gzip())

	// Routes
	e.GET("/serverinfo", serverInfo)

	// Start server
	addr := "127.0.0.1:9090"
	if len(os.Args) > 1 && os.Args[1] == "--addr" {
		addr = os.Args[2]
	}
	e.Start(addr)
}

func serverInfo(c echo.Context) error {
	hostname, err := os.Hostname()
	if err != nil {
		return err
	}
	return c.String(http.StatusOK, fmt.Sprintf("Hostname: %s\nAddress: %s", hostname, strings.Split(c.Request().Host, ":")[0]))
}
