package main

import (
	"sync"

	"github.com/labstack/echo/v4"
	"github.com/olahol/melody"
	"gopkg.in/olahol/melody.v1"
	"gopkg.in/olahol/melody.v1/config"
)

type API struct {
	Messages *ring.Ring
	RWMutex  sync.RWMutex
	Config   *bridge.Config
	mrouter  *melody.Melody
}

func New(cfg *bridge.Config) bridge.Bridger {
	api := &API{
		Config:  cfg,
		mrouter: melody.New(),
	}

	e := echo.New()

	api.mrouter.HandleMessage(api.handleWebsocketMessage)

	e.GET("/ws", api.handleWebsocket)
	e.GET("/health", api.handleHealthcheck)
	e.GET("/messages", api.handleMessages)
	e.GET("/stream", api.handleStream)
	e.POST("/message", api.handlePostMessage)

	go func() {
		e.Start(cfg.Address)
	}()

	return api
}

func (api *API) handleWebsocketMessage(s *melody.Session, msg []byte) {
	message := config.Message{}
	if err := json.Unmarshal(msg, &message); err != nil {
		return
	}

	api.RWMutex.Lock()
	defer api.RWMutex.Unlock()

	api.Messages.Value = message
	api.Messages = api.Messages.Next()

	api.mrouter.Broadcast(msg)
}

func (api *API) handleWebsocket(c echo.Context) error {
	return api.mrouter.HandleRequest(c.Response(), c.Request())
}

func (api *API) handleHealthcheck(c echo.Context) error {
	return c.JSON(200, map[string]string{"status": "ok"})
}

func (api *API) handleMessages(c echo.Context) error {
	api.RWMutex.RLock()
	defer api.RWMutex.RUnlock()

	var messages []config.Message
	api.Messages.Do(func(p interface{}) {
		if p != nil {
			messages = append(messages, p.(config.Message))
		}
	})

	return c.JSON(200, messages)
}

func (api *API) handleStream(c echo.Context) error {
	ch := make(chan config.Message)
	defer close(ch)

	go func() {
		for msg := range ch {
			c.JSON(200, msg)
		}
	}()

	api.mrouter.HandleConnect(func(s *melody.Session) {
		s.Write([]byte("connected"))
	})

	return nil
}

func (api *API) handlePostMessage(c echo.Context) error {
	var msg config.Message
	if err := c.Bind(&msg); err != nil {
		return err
	}

	api.RWMutex.Lock()
	defer api.RWMutex.Unlock()

	api.Messages.Value = msg
	api.Messages = api.Messages.Next()

	api.mrouter.Broadcast([]byte(msg))

	return c.JSON(200, map[string]string{"status": "ok"})
}

