package main

import (
	"net/http"
	"sync"
	"time"

	"github.com/labstack/echo/v4"
)

type AutoManagedProvider struct {
	activeProvider          *echo.Echo
	activeProviderRunning   bool
	activeProviderTesting   bool
	httpClient              *http.Client
	autoManagePort          int
	refreshTTL              time.Duration
	lastActivity            time.Time
	mu                      sync.Mutex
}

func NewWithConfig(refreshTTL time.Duration, autoManPort int, hosts ...string) *AutoManagedProvider {
	return &AutoManagedProvider{
		activeProvider:        echo.New(),
		httpClient:           &http.Client{Timeout: 5 * time.Second},
		autoManagePort:        autoManPort,
		refreshTTL:            refreshTTL,
		lastActivity:         time.Now(),
	}
}

func (a *AutoManagedProvider) UpdateTTL() {
	a.mu.Lock()
	defer a.mu.Unlock()

	a.lastActivity = time.Now()
	if !a.activeProviderRunning {
		a.startServer()
	}
}

func (a *AutoManagedProvider) isShutdown() bool {
	a.mu.Lock()
	defer a.mu.Unlock()

	return time.Since(a.lastActivity) > a.refreshTTL
}

func (a *AutoManagedProvider) startServer() {
	if a.activeProviderRunning {
		return
	}

	a.activeProvider.GET("/health", func(c echo.Context) error {
		return c.JSON(http.StatusOK, a.getCurrentNode())
	})

	go func() {
		a.activeProviderRunning = true
		a.activeProvider.Start(":" + string(a.autoManagePort))
		a.activeProviderRunning = false
	}()
}

func (a *AutoManagedProvider) getCurrentNode() interface{} {
	return map[string]interface{}{
		"status":    "healthy",
		"timestamp": time.Now().Unix(),
		"port":      a.autoManagePort,
	}
}

