package main

import (
	"github.com/labstack/echo/v4"
	"github.com/labstack/echo/v4/middleware"
	"net/http"
)

type Host struct {
	Echo *echo.Echo
}

func SetupReverseProxy() {
	e := echo.New()
	e.Use(middleware.Logger())
	e.Use(middleware.Recover())

	hosts := map[string]*Host{
		"example.com": {Echo: echo.New()},
		"api.com":    {Echo: echo.New()},
	}

	for host, h := range hosts {
		h.Echo.GET("/*", func(c echo.Context) error {
			req := c.Request()
			res := c.Response()
			target := hosts[host].Echo
			target.ServeHTTP(res, req)
			return nil
		})
	}

	e.Any("/*", func(c echo.Context) error {
		req := c.Request()
		host := req.Host
		if h, ok := hosts[host]; ok {
			h.Echo.ServeHTTP(c.Response(), req)
			return nil
		}
		return echo.NewHTTPError(http.StatusNotFound, "Host not found")
	})

	e.Logger.Fatal(e.Start(":8080"))
}

