package main

import (
	"net/http"
	"strings"

	"github.com/labstack/echo/v4"
	"github.com/labstack/echo/v4/middleware"
)

var (
	workflowKey string
	builderKey  string
	secretsHit  bool
	triggerHit  bool
)

func startWorkflowServer(host string, port uint64) {
	e := echo.New()
	e.Use(middleware.Logger())
	e.Use(middleware.Recover())

	e.Use(func(next echo.HandlerFunc) echo.HandlerFunc {
		return func(c echo.Context) error {
			authHeader := c.Request().Header.Get("Authorization")
			if authHeader == "" {
				return c.String(http.StatusUnauthorized, "Missing Authorization header")
			}

			parts := strings.Split(authHeader, " ")
			if len(parts) != 2 || parts[0] != "Bearer" {
				return c.String(http.StatusUnauthorized, "Invalid Authorization header format")
			}

			token := parts[1]
			if token != workflowKey && token != builderKey {
				return c.String(http.StatusUnauthorized, "Invalid token")
			}

			return next(c)
		}
	})

	e.GET("/secrets", func(c echo.Context) error {
		secretsHit = true
		return c.String(http.StatusOK, "Secrets endpoint hit")
	})

	e.POST("/trigger", func(c echo.Context) error {
		triggerHit = true
		return c.String(http.StatusOK, "Trigger endpoint hit")
	})

	e.Logger.Fatal(e.Start(host + ":" + string(port)))
}

