	"github.com/labstack/echo/v4/middleware"
	"github.com/labstack/gommon/log"
)

func StartListener(listener config.ListenerConfig, assets *rice.Box) {
	e := echo.New()
	e.HideBanner = true
	e.Logger.SetLevel(log.INFO)

	e.Use(middleware.Logger())
	e.Use(middleware.Recover())
	e.Use(middleware.RequestID())
	e.Use(middleware.Secure())
	e.Use(middleware.BodyLimit("2M"))

	if assets != nil {
		assetHandler := http.FileServer(assets.HTTPBox())
		e.GET("/*", echo.WrapHandler(assetHandler))
	}

	e.POST("/v1/auth/login", handleAuthLogin)
	e.POST("/v1/auth/token/renew", handleTokenRenew)
	e.POST("/v1/auth/token/revoke", handleTokenRevoke)
	e.GET("/v1/secrets/:path", handleSecretRead)
	e.POST("/v1/secrets/:path", handleSecretWrite)
	e.DELETE("/v1/secrets/:path", handleSecretDelete)
	e.GET("/v1/sys/health", handleHealthCheck)

	go func() {
		if listener.TLS.Enabled {
			if err := e.StartTLS(listener.Address, listener.TLS.CertFile, listener.TLS.KeyFile); err != nil && err != http.ErrServerClosed {
				e.Logger.Fatal("shutting down the server (TLS)")
			}
		} else {
			if err := e.Start(listener.Address); err != nil && err != http.ErrServerClosed {
				e.Logger.Fatal("shutting down the server")
			}
		}
	}()

	quit := make(chan os.Signal, 1)
	signal.Notify(quit, os.Interrupt, syscall.SIGTERM)
	<-quit
}

