package main

import (
	"context"
	"fmt"
	"strings"

	"github.com/alibaba/sentinel-golang/api"
	"github.com/alibaba/sentinel-golang/core/base"
	"github.com/alibaba/sentinel-golang/core/flow"
	"github.com/gofiber/fiber/v2"
)

func initSentinel() {
	err := api.InitDefault()
	if err != nil {
		panic(err)
	}

	_, err = flow.LoadRules([]*flow.Rule{
		{
			Resource:               "ip",
			Threshold:              10,
			TokenCalculateStrategy: flow.Direct,
			ControlBehavior:        flow.Reject,
			StatIntervalInMs:       1000,
		},
	})
	if err != nil {
		panic(err)
	}
}

func extractResource(ctx *fiber.Ctx) string {
	ip := ctx.Get("X-Forwarded-For")
	if ip == "" {
		ip = ctx.Get("X-Real-IP")
	}
	if ip == "" {
		ip = ctx.IP()
	}
	if strings.Contains(ip, ",") {
		ips := strings.Split(ip, ",")
		ip = strings.TrimSpace(ips[0])
	}
	return ip
}

func sentinelMiddleware(c *fiber.Ctx) error {
	resource := extractResource(c)
	entry, err := api.Entry(
		resource,
		api.WithResourceType(base.ResTypeWeb),
		api.WithTrafficType(base.Inbound),
	)
	if err != nil {
		return c.Status(429).JSON(fiber.Map{
			"error":   "too many requests",
			"message": "rate limit exceeded",
		})
	}
	defer entry.Exit()
	return c.Next()
}

func main() {
	initSentinel()

	app := fiber.New()

	app.Use(sentinelMiddleware)

	app.Get("/", func(c *fiber.Ctx) error {
		return c.JSON(fiber.Map{"message": "Hello, World!"})
	})

	if err := app.Listen(":8080"); err != nil {
		panic(err)
	}
}

