	if !s.cfg.Enabled {
		return nil
	}
	return s.App.Shutdown()
}

func (s *SQS) Action(c *fiber.Ctx) error {
	target := c.Get("X-Amz-Target")
	if target == "" {
		return fiber.NewError(fiber.StatusBadRequest, "Missing X-Amz-Target header")
	}

	tenantID := c.Locals("tenantID").(int64)

	switch {
	case strings.HasSuffix(target, "SendMessage"):
		return s.SendMessage(c, tenantID)
	case strings.HasSuffix(target, "SendMessageBatch"):
		return s.SendMessageBatch(c, tenantID)
	case strings.HasSuffix(target, "ReceiveMessage"):
		return s.ReceiveMessage(c, tenantID)
	case strings.HasSuffix(target, "DeleteMessage"):
		return s.DeleteMessage(c, tenantID)
	case strings.HasSuffix(target, "ListQueues"):
		return s.ListQueues(c, tenantID)
	case strings.HasSuffix(target, "GetQueueUrl"):
		return s.GetQueueURL(c, tenantID)
	case strings.HasSuffix(target, "CreateQueue"):
		return s.CreateQueue(c, tenantID)
	case strings.HasSuffix(target, "GetQueueAttributes"):
		return s.GetQueueAttributes(c, tenantID)
	case strings.HasSuffix(target, "PurgeQueue"):
		return s.PurgeQueue(c, tenantID)
	case strings.HasSuffix(target, "ChangeMessageVisibility"):
		return s.ChangeMessageVisibility(c, tenantID)
	default:
		return fiber.NewError(fiber.StatusBadRequest, "Unsupported action")
	}
}

