package main

import (
	"bytes"
	"encoding/json"
	"fmt"
	"io"
	"net/http"
	"net/url"
	"os"
	"time"

	"github.com/gin-gonic/gin"
)

type Config struct {
	IP    string
	Port  string
	Proxy string
	Token string
}

type TranslationRequest struct {
	Text       string `json:"text"`
	SourceLang string `json:"source_lang"`
	TargetLang string `json:"target_lang"`
}

type TranslationResponse struct {
	Data string `json:"data"`
}

func initConfig() Config {
	proxy := os.Getenv("PROXY")
	if proxy == "" {
		proxy = ""
	}
	return Config{
		IP:    "0.0.0.0",
		Port:  "1188",
		Proxy: proxy,
		Token: "",
	}
}

func authMiddleware(c *gin.Context) {
	token := c.GetHeader("Authorization")
	config := initConfig()
	if config.Token != "" && token != config.Token {
		c.JSON(http.StatusUnauthorized, gin.H{"error": "Unauthorized"})
		c.Abort()
		return
	}
	c.Next()
}

func translateHandler(c *gin.Context) {
	var req TranslationRequest
	if err := c.ShouldBindJSON(&req); err != nil {
		c.JSON(http.StatusBadRequest, gin.H{"error": err.Error()})
		return
	}

	config := initConfig()
	client := &http.Client{Timeout: 30 * time.Second}

	if config.Proxy != "" {
		proxyURL, err := url.Parse(config.Proxy)
		if err != nil {
			c.JSON(http.StatusInternalServerError, gin.H{"error": "Invalid proxy URL"})
			return
		}
		client.Transport = &http.Transport{Proxy: http.ProxyURL(proxyURL)}
	}

	payload := map[string]interface{}{
		"text":       req.Text,
		"source_lang": req.SourceLang,
		"target_lang": req.TargetLang,
	}

	jsonData, err := json.Marshal(payload)
	if err != nil {
		c.JSON(http.StatusInternalServerError, gin.H{"error": err.Error()})
		return
	}

	resp, err := client.Post("https://api.deeplx.org/translate", "application/json", bytes.NewBuffer(jsonData))
	if err != nil {
		c.JSON(http.StatusInternalServerError, gin.H{"error": err.Error()})
		return
	}
	defer resp.Body.Close()

	body, err := io.ReadAll(resp.Body)
	if err != nil {
		c.JSON(http.StatusInternalServerError, gin.H{"error": err.Error()})
		return
	}

	var result TranslationResponse
	if err := json.Unmarshal(body, &result); err != nil {
		c.JSON(http.StatusInternalServerError, gin.H{"error": err.Error()})
		return
	}

	c.JSON(http.StatusOK, result)
}

func main() {
	config := initConfig()
	r := gin.Default()

	r.Use(func(c *gin.Context) {
		c.Writer.Header().Set("Access-Control-Allow-Origin", "*")
		c.Writer.Header().Set("Access-Control-Allow-Methods", "GET, POST, OPTIONS")
		c.Writer.Header().Set("Access-Control-Allow-Headers", "Content-Type, Authorization")
		if c.Request.Method == "OPTIONS" {
			c.AbortWithStatus(http.StatusOK)
			return
		}
		c.Next()
	})

	r.GET("/", func(c *gin.Context) {
		c.JSON(http.StatusOK, gin.H{
			"status":  "running",
			"version": "1.0",
		})
	})

	r.POST("/translate", authMiddleware, translateHandler)

	fmt.Printf("Server running on %s:%s\n", config.IP, config.Port)
	if config.Proxy != "" {
		fmt.Printf("Using proxy: %s\n", config.Proxy)
	}
	if config.Token != "" {
		fmt.Println("Authentication enabled")
	}

	r.Run(fmt.Sprintf("%s:%s", config.IP, config.Port))
}

