	DialTimeout    time.Duration
	ReadTimeout    time.Duration
	WriteTimeout   time.Duration
	TsigSecret     map[string]string
	SingleInflight bool
	group          singleflight
}

type Conn struct {
	net.Conn
	UDPSize        uint16
	TsigSecret     map[string]string
	tsigRequestMAC string
}

type singleflight struct {
	inflights map[uint16]*inflight
	mu        sync.Mutex
}

type inflight struct {
	done chan struct{}
	r    *Msg
	err  error
}

func (c *Client) Dial(address string) (*Conn, error) {
	var (
		conn net.Conn
		err  error
	)

	dialer := c.Dialer
	if dialer == nil {
		dialer = &net.Dialer{
			Timeout: c.getTimeoutForRequest(c.DialTimeout),
		}
	}

	ctx := context.Background()
	if c.DialTimeout != 0 {
		var cancel context.CancelFunc
		ctx, cancel = context.WithTimeout(ctx, c.DialTimeout)
		defer cancel()
	}

	switch c.Net {
	case "tcp", "tcp4", "tcp6":
		conn, err = dialer.DialContext(ctx, c.Net, address)
	case "tcp-tls", "tcp4-tls", "tcp6-tls":
		tlsDialer := &tls.Dialer{
			NetDialer: dialer,
			Config:    c.TLSConfig,
		}
		conn, err = tlsDialer.DialContext(ctx, c.Net[:len(c.Net)-4], address)
	default:
		return nil, errors.New("unsupported network type")
	}

	if err != nil {
		return nil, err
	}

	return &Conn{
		Conn:     conn,
		UDPSize:  c.UDPSize,
		TsigSecret: c.TsigSecret,
	}, nil
}

func (c *Client) Exchange(m *Msg, address string) (*Msg, time.Duration, error) {
	if c.SingleInflight {
		t := c.group.Do(m.Id, func() (*Msg, error) {
			r, _, e := c.exchange(m, address)
			return r, e
		})
		return t.r, 0, t.err
	}
	return c.exchange(m, address)
}

func (c *Client) exchange(m *Msg, a string) (*Msg, time.Duration, error) {
	conn, err := c.Dial(a)
	if err != nil {
		return nil, 0, err
	}
	defer conn.Close()

	timeout := c.getTimeoutForRequest(c.WriteTimeout)
	if err := conn.SetWriteDeadline(time.Now().Add(timeout)); err != nil {
		return nil, 0, err
	}

	if err := conn.WriteMsg(m); err != nil {
		return nil, 0, err
	}

	timeout = c.getTimeoutForRequest(c.ReadTimeout)
	if err := conn.SetReadDeadline(time.Now().Add(timeout)); err != nil {
		return nil, 0, err
	}

	r, err := conn.ReadMsg()
	if err != nil {
		return nil, 0, err
	}

	return r, timeout, nil
}

func (c *Client) getTimeoutForRequest(timeout time.Duration) time.Duration {
	if c.Timeout != 0 {
		timeout = c.Timeout
	}
	return timeout
}

func (g *singleflight) Do(id uint16, fn func() (*Msg, error)) *inflight {
	g.mu.Lock()
	if g.inflights == nil {
		g.inflights = make(map[uint16]*inflight)
	}
	flight, ok := g.inflights[id]
	if !ok {
		flight = &inflight{
			done: make(chan struct{}),
		}
		g.inflights[id] = flight
		g.mu.Unlock()

		flight.r, flight.err = fn()
		close(flight.done)

		g.mu.Lock()
		delete(g.inflights, id)
		g.mu.Unlock()
		return flight
	}
	g.mu.Unlock()

	<-flight.done
	return flight
}

func (c *Conn) WriteMsg(m *Msg) error {
	data, err := m.Pack()
	if err != nil {
		return err
	}
	_, err = c.Write(data)
	return err
}

func (c *Conn) ReadMsg() (*Msg, error) {
	var m Msg
	if err := m.Unpack(c); err != nil {
		return nil, err
	}
	return &m, nil
}

