	once     sync.Once
	logger   *log.Logger
	done     atomic.Bool
}

type config struct {
	maxConnections int
	timeout       time.Duration
	idleTimeout   time.Duration
}

type backend struct {
	repos map[string]struct{}
}

func NewGitDaemon(ctx context.Context, addr string, cfg *config, be *backend, logger *log.Logger) (*GitDaemon, error) {
	if cfg == nil {
		return nil, errors.New("config cannot be nil")
	}
	if be == nil {
		return nil, errors.New("backend cannot be nil")
	}
	if logger == nil {
		logger = log.Default()
	}

	return &GitDaemon{
		ctx:      ctx,
		addr:     addr,
		finished: make(chan struct{}),
		cfg:      cfg,
		be:       be,
		logger:   logger,
	}, nil
}

func (gd *GitDaemon) ListenAndServe() error {
	ln, err := net.Listen("tcp", gd.addr)
	if err != nil {
		return err
	}
	defer ln.Close()

	return gd.Serve(ln)
}

func (gd *GitDaemon) Serve(ln net.Listener) error {
	defer gd.cleanup()

	var tempDelay time.Duration
	for {
		conn, err := ln.Accept()
		if err != nil {
			if gd.done.Load() {
				return nil
			}
			if ne, ok := err.(net.Error); ok && ne.Temporary() {
				if tempDelay == 0 {
					tempDelay = 5 * time.Millisecond
				} else {
					tempDelay *= 2
				}
				if max := 1 * time.Second; tempDelay > max {
					tempDelay = max
				}
				gd.logger.Printf("accept error: %v; retrying in %v", err, tempDelay)
				time.Sleep(tempDelay)
				continue
			}
			return err
		}
		tempDelay = 0

		if gd.cfg.maxConnections > 0 && gd.conns.len() >= gd.cfg.maxConnections {
			conn.Close()
			gd.logger.Printf("max connections reached (%d)", gd.cfg.maxConnections)
			continue
		}

		if !gd.conns.add(conn) {
			conn.Close()
			continue
		}

		gd.wg.Add(1)
		go func() {
			defer gd.wg.Done()
			defer gd.conns.remove(conn)
			gd.handleClient(conn)
		}()
	}
}

func (gd *GitDaemon) handleClient(conn net.Conn) {
	defer conn.Close()

	sc := &serverConn{
		Conn:    conn,
		timeout: gd.cfg.idleTimeout,
	}

	service, repoPath, err := gd.readRequest(sc)
	if err != nil {
		gd.logger.Printf("read request error: %v", err)
		return
	}

	if _, exists := gd.be.repos[repoPath]; !exists {
		gd.logger.Printf("repository not found: %s", repoPath)
		return
	}

	switch service {
	case "git-upload-pack":
		gd.handleUploadPack(sc, repoPath)
	case "git-upload-archive":
		gd.handleUploadArchive(sc, repoPath)
	default:
		gd.logger.Printf("unsupported service: %s", service)
	}
}

func (gd *GitDaemon) readRequest(conn *serverConn) (string, string, error) {
	buf := make([]byte, 1024)
	n, err := conn.Read(buf)
	if err != nil {
		return "", "", err
	}

	var service, repoPath string
	_, err = fmt.Sscanf(string(buf[:n]), "%s %s", &service, &repoPath)
	if err != nil {
		return "", "", err
	}

	return service, repoPath, nil
}

func (gd *GitDaemon) handleUploadPack(conn *serverConn, repoPath string) {
	gd.logger.Printf("upload-pack request for %s", repoPath)
	io.WriteString(conn, "0000")
}

func (gd *GitDaemon) handleUploadArchive(conn *serverConn, repoPath string) {
	gd.logger.Printf("upload-archive request for %s", repoPath)
	io.WriteString(conn, "0000")
}

func (gd *GitDaemon) cleanup() {
	gd.once.Do(func() {
		gd.done.Store(true)
		close(gd.finished)
		gd.wg.Wait()
	})
}

func (gd *GitDaemon) Shutdown() {
	gd.cleanup()
}

