func (s *Server) serveTCP(ln net.Listener) error {
	s.lock.Lock()
	s.started = true
	s.lock.Unlock()

	var wg sync.WaitGroup
	defer func() {
		wg.Wait()
		s.lock.Lock()
		for conn := range s.conns {
