func NewServer() (*Server, error) {
	s := &Server{
		closing:        make(chan struct{}),
		nodeID:         1,
		partitionCount: 3,
	}
	l, err := net.Listen("tcp", ":0")
	if err != nil {
		return nil, err
	}
	s.Addr = l.Addr()

	s.prepareBrokerMsg()
	s.wg.Add(1)
	go func() {
		defer s.wg.Done()
		s.run(l)
	}()
	return s, nil
}

func (s *Server) prepareBrokerMsg() {
	host, port, _ := net.SplitHostPort(s.Addr.String())
	s.brokerMessage = make([]byte, 0, 4+2+len(host)+4)
	s.brokerMessage = writeInt32(s.brokerMessage, s.nodeID)
	s.brokerMessage = writeStr(s.brokerMessage, host)
	portN, _ := strconv.Atoi(port)
	s.brokerMessage = writeInt32(s.brokerMessage, int32(portN))
	s.brokerMessage = writeInt16(s.brokerMessage, -1)
}

func (s *Server) Close() {
	s.mu.Lock()
	defer s.mu.Unlock()
	if s.closed {
		return
	}
	s.closed = true
	close(s.closing)
	s.wg.Wait()
}

func (s *Server) run(l net.Listener) {
	defer l.Close()

	accepts := make(chan net.Conn)

	s.wg.Add(1)
	go func() {
		defer s.wg.Done()
		for {
			c, err := l.Accept()
			if err != nil {
				return
			}
			select {
			case accepts <- c:
			case <-s.closing:
				return
			}
		}
	}()

	for {
		select {
		case c := <-accepts:
			go func() {
				defer c.Close()
				for {
					if err := s.handle(c); err != nil {
						if err == io.EOF {
							return
						}
						s.mu.Lock()
						s.errors = append(s.errors, err)
						s.mu.Unlock()
					}
				}
			}()
		case <-s.closing:
			return
		}
	}
}

func (s *Server) handle(c net.Conn) error {
	var size int32
	err := binary.Read(c, binary.BigEndian, &size)
	if err != nil {
		return err
	}

	buf := make([]byte, int(size))

	_, err = io.ReadFull(c, buf)
	if err != nil {
		return err
	}

	apiKey := int16(binary.BigEndian.Uint16(buf[:2]))
	version := int16(binary.BigEndian.Uint16(buf[2:4]))
