)

type Message struct {
	Topic     string
	Partition int32
	Value     []byte
}

type Response struct {
	Partition int32
	Offset    int64
	Error     error
}

type Server struct {
	Addr           net.Addr
	mu             sync.Mutex
	wg             sync.WaitGroup
	closed         bool
	closing        chan struct{}
	messages       []Message
	errors         []error
	brokerMessage  []byte
	nodeID         int32
	partitionCount int32
}

func NewServer(addr string, nodeID int32, partitionCount int32) (*Server, error) {
	listener, err := net.Listen("tcp", addr)
	if err != nil {
		return nil, err
	}

	s := &Server{
		Addr:           listener.Addr(),
		closing:        make(chan struct{}),
		nodeID:         nodeID,
		partitionCount: partitionCount,
	}

	s.prepareBrokerMsg()

	s.wg.Add(1)
	go s.run(listener)

	return s, nil
}

func (s *Server) prepareBrokerMsg() {
	buf := new(bytes.Buffer)
	binary.Write(buf, binary.BigEndian, s.nodeID)
	binary.Write(buf, binary.BigEndian, int32(len(s.Addr.String())))
	buf.WriteString(s.Addr.String())
	s.brokerMessage = buf.Bytes()
}

func (s *Server) Close() {
	s.mu.Lock()
	defer s.mu.Unlock()

	if s.closed {
		return
	}

	close(s.closing)
	s.closed = true
	s.wg.Wait()
}

func (s *Server) run(listener net.Listener) {
	defer s.wg.Done()
	defer listener.Close()

	for {
		select {
		case <-s.closing:
			return
		default:
			conn, err := listener.Accept()
			if err != nil {
				s.mu.Lock()
				s.errors = append(s.errors, err)
				s.mu.Unlock()
				continue
			}

			s.wg.Add(1)
			go func() {
				defer s.wg.Done()
				if err := s.handle(conn); err != nil {
					s.mu.Lock()
					s.errors = append(s.errors, err)
					s.mu.Unlock()
				}
			}()
		}
	}
}

func (s *Server) handle(conn net.Conn) error {
	defer conn.Close()

	reader := bufio.NewReader(conn)
	header := make([]byte, 4)
	if _, err := io.ReadFull(reader, header); err != nil {
		return err
	}

	length := binary.BigEndian.Uint32(header)
	if length == 0 {
		return errors.New("invalid message length")
	}

	msgType := make([]byte, 2)
	if _, err := io.ReadFull(reader, msgType); err != nil {
		return err
	}

	payload := make([]byte, length-2)
	if _, err := io.ReadFull(reader, payload); err != nil {
		return err
	}

	switch binary.BigEndian.Uint16(msgType) {
	case 0:
		topic, responses, err := s.readProduceRequest(payload)
		if err != nil {
			return err
		}

		responseBuf := new(bytes.Buffer)
		for _, r := range responses {
			binary.Write(responseBuf, binary.BigEndian, r.Partition)
			binary.Write(responseBuf, binary.BigEndian, r.Offset)
			binary.Write(responseBuf, binary.BigEndian, int16(0))
		}

		response := new(bytes.Buffer)
		binary.Write(response, binary.BigEndian, int32(responseBuf.Len()))
		response.Write(responseBuf.Bytes())

		if _, err := conn.Write(response.Bytes()); err != nil {
			return err
		}

		s.mu.Lock()
		s.messages = append(s.messages, Message{
			Topic:     topic,
			Partition: responses[0].Partition,
			Value:     payload,
		})
		s.mu.Unlock()

	case 1:
		if _, err := conn.Write(s.brokerMessage); err != nil {
			return err
		}
	default:
		return fmt.Errorf("unknown message type: %d", binary.BigEndian.Uint16(msgType))
	}

	return nil
}

func (s *Server) readProduceRequest(data []byte) (string, []Response, error) {
	reader := bytes.NewReader(data)

	var topicLen int16
	if err := binary.Read(reader, binary.BigEndian, &topicLen); err != nil {
		return "", nil, err
	}

	topicBytes := make([]byte, topicLen)
	if _, err := reader.Read(topicBytes); err != nil {
		return "", nil, err
	}
	topic := string(topicBytes)

	var partitionCount int32
	if err := binary.Read(reader, binary.BigEndian, &partitionCount); err != nil {
		return "", nil, err
	}

	responses := make([]Response, partitionCount)
	for i := int32(0); i < partitionCount; i++ {
		var partition int32
		if err := binary.Read(reader, binary.BigEndian, &partition); err != nil {
			return "", nil, err
		}

		var msgSize int32
		if err := binary.Read(reader, binary.BigEndian, &msgSize); err != nil {
			return "", nil, err
		}

		msg := make([]byte, msgSize)
		if _, err := reader.Read(msg); err != nil {
			return "", nil, err
		}

		responses[i] = Response{
			Partition: partition,
			Offset:    int64(len(s.messages)),
		}
	}

	return topic, responses, nil
}

func (s *Server) readStrList(data []byte) ([]string, int) {
	reader := bytes.NewReader(data)

	var count int32
	if err := binary.Read(reader, binary.BigEndian, &count); err != nil {
		return nil, 0
	}

	strs := make([]string, count)
	for i := int32(0); i < count; i++ {
		var strLen int16
		if err := binary.Read(reader, binary.BigEndian, &strLen); err != nil {
			return nil, 0
		}

		strBytes := make([]byte, strLen)
		if _, err := reader.Read(strBytes); err != nil {
			return nil, 0
		}

		strs[i] = string(strBytes)
	}

	return strs, reader.Len()
}

