	Msg []byte
}

type DecorateReader func(io.Reader) io.Reader
type DecorateWriter func(io.Writer) io.Writer
type MsgAcceptFunc func([]byte) error
type TsigProvider func() ([]byte, error)

type Server struct {
	Addr              string
	Net               string
	Listener          net.Listener
