	MsgAcceptFunc     MsgAcceptFunc
	lock              sync.RWMutex
	started           bool
	shutdown          chan struct{}
	conns             map[net.Conn]struct{}
	udpPool           sync.Pool
}

func (s *Server) ListenAndServe() error {
	if s.isStarted() {
		return errors.New("server already started")
	}

