	var err error

	if s.Net == "" {
		s.Net = "tcp"
	}

	switch s.Net {
	case "tcp", "tcp4", "tcp6":
		l, err = net.Listen(s.Net, s.Addr)
	case "tcp-tls", "tcp4-tls", "tcp6-tls":
		if s.TLSConfig == nil {
			return errors.New("TLSConfig must be set for TLS")
		}
		l, err = tls.Listen(s.Net[:3], s.Addr, s.TLSConfig)
	default:
		return errors.New("unknown network type")
	}

	if err != nil {
		return err
	}

	s.Listener = l
	s.started = true

	if s.NotifyStartedFunc != nil {
		s.NotifyStartedFunc()
	}

	return s.serveTCP(l)
}

func (s *Server) serveTCP(l net.Listener) error {
	defer l.Close()

	var wg sync.WaitGroup
	defer func() {
		s.lock.Lock()
		for conn := range s.conns {
			conn.Close()
		}
		s.lock.Unlock()
		wg.Wait()
	}()

